var populate_dents=function(a){if(!a){return false}$.ajax({url:"http://identi.ca/api/statusnet/tags/timeline/"+a+".json",dataType:"json",success:function(d){if(d!=null&&d.length>0){$(".related").before('<h2 id="replies">Replies to This Post</h2>');$("#replies").after('<p>&#x272A; Reply to this post on <a href="http://identi.ca">identi.ca</a> using the hash tag <strong>#'+a+'</strong></p><ol class="dents"></ol>');$(".dents").css("font-size",".9em");$("#replies").css("margin-top","2em");var c=[];for(i in d){c=d[i];var b=c.created_at.replace(/\d\d:\d\d:\d\d \+0000/g,"");$(".dents").append("<li>&#x231A; <time>"+b+'</time><br><a href="http://identi.ca/notice/'+c.id+'">'+c.user.name+"</a> "+c.statusnet_html+"</li>")}}else{$(".related").before('<aside><p style="margin-top:2em;">&#x272A; Reply to thispost on <a href="http://identi.ca">identi.ca</a> using the hash tag <strong>#'+a+"</strong></p></aside>")}}})};var easterEggKeys=new Array("j","i","b","b","y");var easterEggCurrent=0;$(window).keydown(function(b){var a=String.fromCharCode(b.keyCode).toLowerCase();if(easterEggKeys[easterEggCurrent].toLowerCase()==a){easterEggCurrent++;if(easterEggCurrent>=easterEggKeys.length){$("body").css("background","transparent url(/system/admin/images/pasaka.png) no-repeat 80% 150px");easterEggCurrent=0}}else{easterEggCurrent=0}});var adminKeys=new Array("g","o","i","n");var adminCurrent=0;$(window).keydown(function(b){var a=String.fromCharCode(b.keyCode).toLowerCase();if(adminKeys[adminCurrent].toLowerCase()==a){adminCurrent++;if(adminCurrent>=adminKeys.length){document.location="/admin";adminCurrent=0}}else{adminCurrent=0}});$(document).ready(function(){$("a[href=#top]").click(function(){$("html, body").animate({scrollTop:0},"slow");return false})});